set namespace=%request.Get("NameSpace","") set SubPackage=%request.Get("SubPackage","") #dim lenSubPackage=0 s ns=$namespace s $namespace=namespace set pObject=##class(%ZEN.proxyObject).%New() set pObject.children=##class(%ListOfDataTypes).%New() set sql="SELECT * FROM %Dictionary.ClassDefinition " if SubPackage'="" { if ($E(SubPackage,1)=..#Replace){ set SubPackage=$REPLACE(SubPackage,..#Replace,"%",1,1) } set lenSubPackage=$LL($LISTFROMSTRING(SubPackage,".")) set sql=sql_"where ID %STARTSWITH '"_SubPackage_".'" }
set sttm=##class(%SQL.Statement).%New() set sttm.%ObjectSelectMode=1 set st=sttm.%Prepare(sql) set res=sttm.%Execute() set tList=""
while res.%Next(){ s tObj=##class(%ZEN.proxyObject).%New() s tlName=$LISTFROMSTRING(res.%Get("Name"),".")
if (($LL(tlName)-lenSubPackage)>1){ s tST=1 s sp=$LISTTOSTRING($LIST(tlName,1,lenSubPackage+1),".") if $LF(tList,sp)'=0{ s tST=0 } if tST { s tObj.isFolder=1 s tObj.DisplayName=$LISTTOSTRING($LIST(tlName,lenSubPackage+1,lenSubPackage+1),".") s tObj.Name=$REPLACE($LISTTOSTRING($LIST(tlName,1,lenSubPackage+1),"."),"%",..#Replace,1,1) do pObject.children.Insert(tObj) s $LIST(tList,$LL(tList)+1)=sp } } else{ s tObj.isFolder=0 s tObj.DisplayName=$LISTTOSTRING($LIST(tlName,lenSubPackage+1,*),".") s tObj.Name=$REPLACE(res.%Get("Name"),"%",..#Replace,1,1) s tObj.relativePath=..#Class_"/"_$REPLACE(res.%Get("Name"),".","/")_".CLS" s tObj.Target="class" do pObject.children.Insert(tObj) } } do pObject.%ToJSON() s $namespace= ns q $$$OK

set NameSpace=%request.Get("NameSpace","")
set SubPackage=%request.Get("SubPackage","")
s ns=$namespace
s $namespace=NameSpace
s SubPackagelen=$LL($LISTFROMSTRING(SubPackage,"."))
set pObject=##class(%ZEN.proxyObject).%New()
set pObject.children=##class(%ListOfDataTypes).%New()
set tList=""
set sql="SELECT * FROM %Library.RoutineIndex where NOT Name %STARTSWITH '%' and NOT Name %STARTSWITH 'INFORMATION' and Type !='OBJ' "

if SubPackage'="" {
	s sql=sql_" and Name %STARTSWITH '"_SubPackage_".'"
}
set sttm=##class(%SQL.Statement).%New()
set sttm.%ObjectSelectMode=1
set st=sttm.%Prepare(sql)
set res=sttm.%Execute()
while(res.%Next()'=0){
	s tObj=##class(%ZEN.proxyObject).%New()
	s Name=res.%Get("Name")
	s tName=$LISTFROMSTRING(Name,".")
	s jump=0
	s tST=1
	if $ISVALIDNUM($LIST(tName,$LL(tName))){
		s jump=1
	}
	if (($LL(tName)-jump-SubPackagelen)>1){
		for i=1:1:$LL(tList){
	 		if ($FIND($LISTTOSTRING($LIST(tName,1,SubPackagelen+1),"."),$LI(tList,i))'=0){
		 		s tST=0
		 	}
	 	}
	 	if (tST) {
		s tObj.Name=$LISTTOSTRING($LIST(tName,1,SubPackagelen+1),".")
		s tObj.DisplayName=$LISTTOSTRING($LIST(tName,SubPackagelen+1,SubPackagelen+1),".")
		s tObj.isFolder=1
		do pObject.children.Insert(tObj)
		s $LIST(tList,$LL(tList)+1)=$LISTTOSTRING($LIST(tName,1,SubPackagelen+1),".")
		}

	}
	else{
		s tObj.Name=$LISTTOSTRING($LIST(tName,1,*),".")_"."_res.%Get("Type")
		s tObj.DisplayName=$LISTTOSTRING($LIST(tName,SubPackagelen+1,*),".")_"."_res.%Get("Type")
		s tObj.isFolder=0
		s tObj.relativePath=..#Routine_"/"_$LISTTOSTRING($LIST(tName,1,$LL(tName)-jump),"/")_"."_res.%Get("Type")
		s tObj.Target="routine"
		do pObject.children.Insert(tObj)
	}

}
do pObject.%ToJSON()
s $namespace= ns
q $$$OK

